import React from "react";
import Box from "./Box";
import RedBox from "./RedBox";
import "./LandingPage.css";

class LandingPage extends React.Component {
  constructor() {
    super();
    this.state = {
      name: "React",
      total1: 0,
      total2:0,
      totalInput1: "",
      totalInput2: "",
      show: false,
    };
  }

  generate = () => {
    this.setState({
      total1: this.state.totalInput1,
      total2: this.state.totalInput2,
    });
  };

  clear = () => {
    this.setState({
    //   total: this.state.totalInput,
    total1:false,
    total2:false,
    });
  };

  showValues = () => {
    this.setState({
      show: true,
    });
  };

  onChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  render() {
    const inputs1 = [];
    const inputs2 = [];

    for (let i = 1; i <= this.state.total1; i++) {
      inputs1.push(
        <Box />
        //   <input name={`input-${i}`} onChange={this.onChange} />
      );

    }
    for (let i = 1; i <= this.state.total2; i++) {

        inputs2.push(
          <RedBox />
          //   <input name={`input-${i}`} onChange={this.onChange} />
        );
      }
    return (
      <div className="header">
        <h1 className="header__title">Token Generator</h1>
        <form className="form">
          <label>
            Number of blue tokens
            <input
              type="number"
                name="redNum"
                max="10"
              onChange={(e) => this.setState({ totalInput1: e.target.value })}
              value={this.state.totalInput1 <=10}
              placeholder="Enter Number"
            />
          </label>
          <label>
            Number of red tokens
            <input
              type="number"
              //   name="blueNum"
              max="10"
                onChange={(e) => this.setState({ totalInput2: e.target.value })}
                value={this.state.totalInput2 <=10}
              placeholder="Enter Number"
            />
          </label>

        </form>
        <div className="form__button">
            <button onClick={this.clear}>Clear</button>
            <button onClick={this.generate}>Generate</button>
           <div className="form__boxes">
            {inputs1}    
            {inputs2}
            </div>
          </div>
      </div>
      // <div>
      //   <input onChange={(e) => this.setState({ totalInput: e.target.value})} value={this.state.totalInput}  placeholder="Enter Number" />
      //   <button onClick={this.generate}>Generate</button>
      //   <button onClick={this.clear}>Clear</button>
      //   <br />
      //   {inputs}
      //    <br />
      // </div>
    );
  }
}

export default LandingPage;
