    constructor() {
      super();
      this.state = {
        name: 'React',
        total: 0,
        totalInput: '',
        show: false,
      };
    }

    generate = () => {
      this.setState({
        total: this.state.totalInput
      })
    }

    clear = () => {
        this.setState({
          total: this.state.totalInput
        })
      }

    showValues = () => {
      this.setState({
        show: true
      })
    }

    onChange = (event) => {
      this.setState({
        [event.target.name]: event.target.value
